import chalk from 'chalk';
import sym from 'log-symbols';
let green = chalk.hex('#0abf53');
let greenI = chalk.hex('#0abf53').inverse;
let red = chalk.hex('#ff0000');
let redI = chalk.hex('#ff0000').inverse;
let orange = chalk.hex('#FFA500');
let orangeI = chalk.hex('#FFA500').inverse;
let blue = chalk.hex('#037ef3');
let blueI = chalk.hex('#037ef3').inverse;

const alert = options => {
    const defaultOptions = {
        type: `error`,
        msg: `You forgot to define all options`,
        name: ``
    };
    const opts = { ...defaultOptions, ...options };
    const { type, msg, name } = opts;
    let priName = name ? name : type.toUpperCase();

    if (type === `success`) {
        console.log(`\n${sym.success} ${greenI(priName)} ${green(msg)}`);
    }
    if (type === `info`) {
        console.log(`\n${sym.info} ${blueI(priName)} ${blue(msg)}`);
    }
    if (type === `warning`) {
        console.log(`\n${sym.warning} ${orangeI(priName)} ${orange(msg)}`);
    }
    if (type === `error`) {
        console.log(`\n${sym.error} ${redI(priName)} ${red(msg)}`);
    }
};

export default alert;
